fn game_end_event_with_game_over_when_moving_right() { // Setup let mut app = App::new(); // Add systems app.insert_resource(Segments::default()) .insert_resource(LastTailPosition::default()) .add_event::<GameEndEvent>() .add_startup_system(snake::spawn_system) .add_system(snake::movement_system) .add_system(snake::movement_input_system.before(snake::movement_system)) .add_system(game_over_system.after(snake::movement_system)); // Add new input resource let mut input = Input::<KeyCode>::default(); input.press(KeyCode::D); app.insert_resource(input); // Run systems again app.update(); // x: 4, y: 3 app.update(); // x: 5, y: 3 app.update(); // x: 6, y: 3 app.update(); // x: 7, y: 3 app.update(); // x: 8, y: 3 app.update(); // x: 9, y: 3 app.update(); // x: 10, y: 3 let mut query = app.world.query::<&GameEndEvent>(); assert_eq!(query.iter(&app.world).count(), 1);
} pub fn movement_system( // ...
) { // ... if let Some((id, head)) = heads.iter().next() { // ... if game_end.is_empty() { let _ = positions.get_mut(id).map(|(_, _segment, mut pos)| { match &head.direction { // ... }; if pos.x < 0 || pos.y < 0 || pos.x as u16 >= GRID_WIDTH // <-- Nova verifica√ß√£o || pos.y as u16 >= GRID_HEIGHT { game_end_writer.send(GameEndEvent::GameOver); } }); } // ... }
