} fn scale_sprite(transform: &mut Transform, sprite_size: &Size, window: &Window) { transform.scale = Vec3::new( sprite_size.width / GRID_WIDTH as f32 * window.width() as f32, sprite_size.height / GRID_HEIGHT as f32 * window.height() as f32, 1.0, );
} #[cfg(test)]
