#[allow(clippy::needless_pass_by_value)]
pub fn movement_system( keyboard_input: Res<Input<KeyCode>>, mut head_positions: Query<&mut Position, With<Head>>,
) { for mut position in head_positions.iter_mut() { if keyboard_input.pressed(KeyCode::D) { position.x += 1; } if keyboard_input.pressed(KeyCode::W) { position.y += 1; } if keyboard_input.pressed(KeyCode::A) { position.x -= 1; } if keyboard_input.pressed(KeyCode::S) { position.y -= 1; } }
