use bevy::{prelude::*, window::PrimaryWindow}; // ... #[allow(clippy::missing_panics_doc)]
#[allow(clippy::needless_pass_by_value)]
pub fn size_scaling(primary_window: Query<&Window, With<PrimaryWindow>>, mut q: Query<(&Size, &mut Transform)>) { let window = primary_window.get_single().unwrap(); for (sprite_size, mut transform) in q.iter_mut() { scale_sprite(transform.as_mut(), sprite_size, window); }
} #[allow(clippy::missing_panics_doc)]
#[allow(clippy::needless_pass_by_value)]
pub fn position_translation(primary_window: Query<&Window, With<PrimaryWindow>>, mut q: Query<(&Position, &mut Transform)>) { let window = primary_window.get_single().unwrap(); for (pos, mut transform) in q.iter_mut() { translate_position(transform.as_mut(), pos, window); }
}
// ... Existe mais uma mudança relacionada a Window no codigo, que eh no módulo main, ao adicionarmos o plugin de window, WindowPlugin, pois a forma de declarar a window mudou para: fn main() { App::new() .add_plugins(DefaultPlugins.set(WindowPlugin { primary_window: Some(Window { resolution: (1000., 1000.).into(), title: "Snake Game".to_string(), ..default() }), exit_condition: ExitCondition::OnAllClosed, close_when_requested: true, })) // ...
} Agora vamos terminar o upgrade do modulo snake.rs.

